# -*- coding: utf-8 -*-"""Endpoints for some utilities of database."""from flask import Blueprint, jsonifyfrom application import loggerfrom application.database.functions import init_dbbp_util = Blueprint("bp_util", __name__)@bp_util.route("/init", methods=["POST"])def init():    try:        init_db()    except Exception as e:        logger.exception(e)        return jsonify({"status": "failure", "detail": str(e)})    finally:        return jsonify({"status": "ok"})