from flask import Blueprint, jsonifyfrom application.reqs import req_database_rest_getbp_database_rest = Blueprint("bp_database_rest", __name__,                             url_prefix="/database-rest")@bp_database_rest.route("/<string:resource>", methods=["GET"])@bp_database_rest.route("/<string:resource>/<list:pkeys>", methods=["GET"])def resource_get(resource: str, pkeys: list = None):    return jsonify(req_database_rest_get(resource, pkeys))