from flask import Blueprint, request, jsonifyfrom application.rests.elasticsearch import searchbp_elasticsearch = Blueprint("bp_elasticsearch", __name__,                             url_prefix="/elasticsearch")@bp_elasticsearch.route("/search/publication", methods=["GET"])def search_publication():    index = "publication"    text = request.args["text"]    text_field = request.args.get("text_field", None)    vector_field = request.args.get("vector_field", None)    result = search(index, text, text_field, vector_field)    return jsonify(result)