import requestsfrom flask import current_appfrom application.utils.contextor import ensure_app_context@ensure_app_contextdef req_database_rest_get(resource: str, pkeys: list = None):    resd = {"status": "ok"}    try:        url = current_app.config["DATABASE_REST"] + f'/{resource}'        if pkeys:            url += f'/{",".join(pkeys)}'        resd["response"] = requests.get(url).json()    except Exception as e:        resd["status"] = "failure"        resd["failure_detail"] = str(e)