import requests as rqfrom application.utils.helpers import get_configdef search(index, text, text_field="content", vector_field="fasttext_vector"):    url = get_config("VECTORIZER") + "/vectorize"    vectorization = rq.get(url, json={"text": text}).json()    query_json = {        "query": {            "script_score": {                "match": {                    text_field: {                        "query": text                    }                }            },            "script": {                "source": f"cosineSimilarity(params.query_vector, "                          f"'{vector_field}') + 1.0",                "params": {                    "query_vector": vectorization["vector"]                }            }        }    }    url = get_config("ELASTICSEARCH") + f'/{index}/_search'    response = rq.get(url, json=query_json).json()    return response