import requestsfrom application.utils.helpers import get_configdef start_task(task_name, args=None, kwargs=None, extra=None):    url = get_config("WORKER_REST")    url += f'/task/{task_name}/start'    data = {"args": args}    if kwargs: data["kwargs"] = kwargs    if kwargs: data["extra"] = extra    response = requests.post(        url=url,        json={"args": args or [], "kwargs": kwargs or {}, "extra": extra or {}}    ).json()    return response