from werkzeug.routing import BaseConverterclass ListConverter(BaseConverter):    def to_python(self, value):        splitted = value.split(",")        return [v.strip() for v in splitted]    def to_url(self, values):        return ','.join(values)class IntListConverter(BaseConverter):    def to_python(self, value):        splitted = value.split(",")        return [int(v.strip()) for v in splitted]    def to_url(self, values):        return ','.join(values)