from flask import jsonifyfrom flask import Response as FlaskResponsefrom werkzeug.wrappers import Response as WerkzeugResponsedef json_response(function):    """Decorator function to return json response for view functions.    Args:        function: A function that will be wrapped with context.    """    def wrapper(*args, **kwargs):        """Decorate function here.        Args:            *args: Arguments of the function.            **kwargs: Keyword arguments of the function.        Returns:            :obj:`function`: Wrapped function.        """        result = function(*args, **kwargs)        if isinstance(result, tuple):            first_arg = jsonify(result[0])            return (first_arg, *result[1:])        elif isinstance(result, (FlaskResponse, WerkzeugResponse)):            return result        else:            return jsonify(result)    # To avoid naming conflicts.    wrapper.__name__ = function.__name__    return wrapper