import requestsfrom application.app import loggersubscription_key = "09fff28b119542b4a1d5db988dbcacc8"def atranslate(text, dest_lang):    try:        url = f'https://testlabtranslate.cognitiveservices.azure.com' \              f'/translator/text/v3.0/translate?api-version=3.0&to={dest_lang}'        headers = {            "Ocp-Apim-Subscription-Key": subscription_key,            "Content-type": "application/json"        }        # You can pass more than one object in body.        body = [{"text": text}]        return requests.post(url, headers=headers, json=body).json()[0]["translations"][0]["text"]    except Exception as e:        logger.exception(e)        return Falsedef adetect(text):    try:        url = f'https://testlabtranslate.cognitiveservices.azure.com' \              f'/translator/text/v3.0/detect?api-version=3.0&'        headers = {            "Ocp-Apim-Subscription-Key": subscription_key,            "Content-type": "application/json"        }        # You can pass more than one object in body.        body = [{"text": text}]        return requests.post(url, headers=headers, json=body).json()[0]["language"]    except Exception:        return False