from string import ascii_lowercase, digitsfrom flask import current_appfrom application.utils.contextor import ensure_app_context@ensure_app_contextdef get_config(name):    return current_app.config.get(name, None)@ensure_app_contextdef set_config(name, value):    current_app.config[name] = valuedef clean_text(text: str):    accepted_chars = ascii_lowercase + digits + "_ " + " "    s1 = " ".join(text.split())    s2 = "".join([c for c in s1 if c in accepted_chars])    return s2def tokenize(s):    return [w.strip() for w in s.split()]