# -*- coding: utf-8 -*-"""Run application using `Gunicorn`_... _Gunicorn:    http://docs.gunicorn.org/en/stable/"""import osif __name__ == '__main__':    os.environ["LC_ALL"] = "C.UTF-8"    os.environ["LANG"] = "C.UTF-8"    expose_port = os.getenv("EXPOSE_PORT") or 9000    os.system(f'uvicorn '              f'application.app:app '              f'--host 0.0.0.0 '              f'--port {expose_port}')