import requestsfrom application import loggerfrom application.utils.helpers import get_configdef insert_one(collection_name, document):    url = get_config("MONGO_REST")    url += f'/{collection_name}/insert/one'    response = requests.post(        url=url,        json={"document": document}    )    return response.json()["result"]def find(collection_name, data=None):    url = get_config("MONGO_REST")    url += f'/{collection_name}/find'    response = requests.get(        url=url,        json=data or dict()    ).json()    return response.get("result", list())def find_one(collection_name, data):    url = get_config("MONGO_REST")    url += f'/{collection_name}/find/one'    response = requests.get(        url=url,        json=data    ).json()    return response["result"]def update_one(collection_name, data):    url = get_config("MONGO_REST")    url += f'/{collection_name}/update/one'    response = requests.patch(        url=url,        json=data    ).json()    return response["result"]def update_many(collection_name, data):    url = get_config("MONGO_REST")    url += f'/{collection_name}/update/many'    response = requests.patch(        url=url,        json=data    ).json()    return response["result"]def count_documnets(collection_name, data=None):    url = get_config("MONGO_REST")    url += f'/{collection_name}/count/documents'    response = requests.get(        url=url,        json=data or {"filter": {}}    ).json()    return response["result"]def create_index(collection_name, keys, name, background=True, unique=True,                 sparse=True):    url = get_config("MONGO_REST")    url += f'/{collection_name}/create/index'    requests.post(url, json={        "keys": keys,        "name": name,        "background": background,        "unique": unique,        "sparse": sparse    })