import osimport pdfplumberfrom application import celery, logger, ROOT_DIRfrom application.utils.helpers import clean_text@celery.task(bind=True, name="pdf_to_text_by_file_path")def task_pdf_to_text_by_file_path(self, file_path: str = None):    resd = {"status": "ok"}    try:        text_of_pages = ""        pdf = pdfplumber.open(file_path)        for page in pdf.pages:            text_of_pages += page.extract_text()        pdf.close()        resd["content"] = clean_text(text_of_pages)    except Exception as e:        logger.exception(e)        resd["status"] = "failure"        resd["failure_detail"] = str(e)    return resd