from application import celery, loggerfrom application.rests.mongo import findfrom application.tasks.scrape_publications_of_author import \    t_scrape_publications_of_authorfrom application.utils.decorators import celery_exception_handler@celery.task(bind=True, name="publications_scraper", max_retries=3)@celery_exception_handler(ConnectionError)def task_publications_scraper(self):    resd = {"status": "ok"}    authors = find("author")    len_authors = len(authors)    resd["num_authors"] = len_authors    if not authors:        return resd    logger.info(f'# Authors: {len_authors}')    for author in authors:        t_scrape_publications_of_author.apply_async((author["id"], author["name"]))    return resd