from application import celery, loggerfrom application.rests.mongo import findfrom application.tasks.scrape_publications_of_author import \    task_scrape_publications_of_author@celery.task(bind=True, name="publications_scraper")def task_publications_scraper(self):    resd = {"status": "ok"}    authors = find("author")    len_authors = len(authors)    # TODO: Remove here before prod. This is only for development.    if len_authors > 3:        authors = authors[:3]    resd["num_authors"] = len_authors    if not authors:        return resd    logger.info(f'# Authors: {len_authors}')    for author in authors:        logger.info("Author: ", author)        task_scrape_publications_of_author.apply_async((author,))    return resd