import jsonimport requests as reqdata = list()for i in range(0, 10000, 10):    try:        data += req.get(            "http://localhost:9200/publication/_search",            json={"query": {"match_all": {}}, "size": 10, "from": i}        ).json()["hits"]["hits"]    except Exception as e:        print(e)        breakwith open("pubs_es.json", "wt+", encoding="utf-8") as fp:    json.dump(data, fp)